import React, { Component } from 'react'
import $ from 'jquery'
import "./Events.css";


export default class Events extends Component {
  constructor(props) {
    super(props);
  }

  componentDidMount = () => {
    $('.flip').hover(function(){
      $(this).find('.card').toggleClass('flipped');

});
  };

  render() {

    return (
      <div>
        <div class="container">
<div class="row">
<div class="col-sm-3">
      <div class="flip">
        <div class="card"> 
          <div class="face front"> 
            <div class="inner">   
              <img src="https://images.pexels.com/photos/1023756/pexels-photo-1023756.jpeg?auto=compress&cs=tinysrgb&h=650&w=940" />
            </div>
          </div> 
          <div class="face back"> 
            <div class="inner text-center"> 
              <h3>Improved efficiency through automation</h3>
              <button type="button" class="btn btn-default">Know More</button>
            </div>
          </div>
        </div>	 
      </div>
    </div>
  </div>
  </div>
  </div>
    )
  }
}
